import React, { useState } from 'react';
import '../styles/componentStyle/modalHome.scss';

const ModalHomeScreen = ({ isVisible, setIsVisible, type }) => {


    const onClick = () => {
        setIsVisible(false)
    }

    const getTitle = (type) => {
        switch (type) {
            case 1:
                return "Inscription"
            case 2:
                return "Connexion";
            default:
                return null
        }
    };


    if (!isVisible) return null;

    return (
        <div className="modal-overlay">
            <div className="modal">
                <div className="modal-header">
                    <h2>{getTitle(type)}</h2>
                    <button className="close-button" onClick={onClick}>
                        <div className="close-circle"></div>
                    </button>
                </div>
                <div className="modal-content">
                    <input type="text" placeholder="Pseudo" />
                    <input type="password" placeholder="Mot de passe" />
                    {type === 1 &&
                        <input type="password" placeholder="Répéter le mot de passe" />}
                    <button>{getTitle(type)}</button>
                </div>
            </div>
        </div>
    );
};

export default ModalHomeScreen;
