import React from 'react';

const Building = () => {
    return (
        <div className="containerBuild">

        </div>
    );
};

export default Building;


const styles = `
/* Building.css */

.containerBuild {
  width: 10vw;
  height: 40vh;
  background-color: grey;
  border-radius: 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 10px;
}

.name {
  text-align: center;
}

.level {
  text-align: center;
}

.stats {
  display: flex;
  flex-direction: column;
  margin-top: 10px;
}

.column {
  display: flex;
  justify-content: space-between;
  margin-bottom: 5px;
}

.buyButton {
  width: 100%;
  padding: 10px;
  background-color: blue;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

.footer {
  text-align: center;
}

`;

const styleTag = document.createElement('style');
styleTag.innerHTML = styles;
document.head.appendChild(styleTag);
