import React, { useState } from 'react';
import '../styles/componentsStyle/modalHome.scss';

const ModalHomeScreen = ({ isVisible, setIsVisible }) => {


    const onClick = () => {
        setIsVisible(false)
    }


    if (!isVisible) return null;

    return (
        <div className="modal-overlay">
            <div className="modal">
                <div className="modal-header">
                    <h2>INSCRIPTION</h2>
                    <button className="close-button" onClick={onClick}>
                        <div className="close-circle"></div>
                    </button>
                </div>
                <div className="modal-content">
                    <input type="text" placeholder="Pseudo" />
                    <input type="password" placeholder="Mot de passe" />
                    <input type="password" placeholder="Répéter le mot de passe" />
                    <button>Valider</button>
                </div>
            </div>
        </div>
    );
};

export default ModalHomeScreen;
