import React from 'react';

const HomeGameScreen = () => {
    return (
        <section className={"containerGame"}>

        </section>
    );
};

export default HomeGameScreen;


const styles = `

.containerGame {
  width: 100vw;
  height: 100vw;
  background-color: black;
}

.name {
  text-align: center;
}

.level {
  text-align: center;
}

.stats {
  display: flex;
  flex-direction: column;
  margin-top: 10px;
}

.column {
  display: flex;
  justify-content: space-between;
  margin-bottom: 5px;
}

.buyButton {
  width: 100%;
  padding: 10px;
  background-color: blue;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

.footer {
  text-align: center;
}

`;

const styleTag = document.createElement('style');
styleTag.innerHTML = styles;
document.head.appendChild(styleTag);
