import React, {useState} from 'react';
import { NavLink} from "react-router-dom";
import ModalHomeScreen from "../Components/ModalHomeScreen";

const Navigation = () => {

    const [isVisible, setIsVisible] = useState(false)
    const [type, setType] = useState(null) // 1 = REGISTER / 2 = LOGIN

    const onRegister = () => {
        setIsVisible(true)
        setType(1)
    }

    const onLogin = () => {
        setIsVisible(true)
        setType(2)
    }

    return (
        <div className="navigation">
            <div className="menu">
            <ul className="navigationUL">
                <NavLink to="/">
                    <li className="navigationLI"><a>Accueil</a></li>
                </NavLink>
                <NavLink to="/book">
                    <li className="navigationLI">
                        <a onClick={onRegister}>Inscription</a>
                    </li>
                </NavLink>
                <NavLink to="/news">
                    <li className="navigationLI"><a onClick={onLogin}>Connexion</a></li>
                </NavLink>
                <NavLink to="/test">
                    <li className="navigationLI"><a>Testpage</a></li>
                </NavLink>
            </ul>
                <ModalHomeScreen
                    isVisible={isVisible}
                    setIsVisible={setIsVisible}
                    type={type}
                />
            </div>
        </div>
    );
};

export default Navigation;
