// Building.js

import React from 'react';
import './Building.css'; // Import du fichier CSS externe

const Building = () => {
    return (
        <div className="containerBuild">
            <div className="name">Nom du bâtiment</div>
            <div className="level">Niveau 1</div>
            <img src="chemin/vers/votre/image.jpg" alt="Image du bâtiment" width="120" height="120" />
            <div className="stats">
                <div className="column">
                    <span>Gold:</span> 0
                </div>
                <div className="column">
                    <span>Iron:</span> 0
                </div>
                <div className="column">
                    <span>Temps de construction:</span> 15
                </div>
            </div>
            <button className="buyButton" onClick={() => handleBuyButtonClick()}>
                Acheter
            </button>
            <div className="footer">Contenu du footer</div>
        </div>
    );
};

export default Building;
