import React, { useState, useEffect } from 'react';
import imageCenterPNG from "../../Assets/Image/Header/center.png";


const ContainerResource = ({ backgroundImage, imageCenterPNG, imageCenter, headerLeft }) => {
    const [resources, setResources] = useState([
        {
            id: 1,
            type: 'gold',
            amount: 0,
            production: 1,
        },
        {
            id: 2,
            type: 'iron',
            amount: 0,
            production: 2,
        },
        {
            id: 3,
            type: 'energy',
            amount: 0,
            production: 0.5,
        },
    ]);

    useEffect(() => {
        const interval = setInterval(() => {
            resources.forEach((resource) => { //PARCOURS LE TABLEAU
                increaseResource(resource.id, resource.production);
            });
        }, 1000);
        return () => clearInterval(interval);
    }, [resources]);


    const increaseResource = (id, amount) => {
        setResources((prevResources) =>
            prevResources.map((resource) =>
                resource.id === id ? { ...resource, amount: resource.amount + amount } : resource
            )
        );
    }

    {/*{resources.map((resource) => (*/}
    {/*    <div key={resource.id} className="containerMiniResource">*/}
    {/*      <span className="resourceText">*/}
    {/*        {resource.type}: {resource.amount}*/}
    {/*      </span>*/}
    {/*    </div>*/}
    {/*))}*/}

    return (
        // <div className="containerResource"
        //      style={{
        //          backgroundImage: `url(${backgroundImage})`,
        //          backgroundRepeat: 'no-repeat',
        //          backgroundPosition: 'center',
        //          zIndex: 0
        //      }}
        // >
            <div className={"containerResource"}>
                <div
                    className={"containerHeaderLeft"}
                     style={{
                         backgroundImage: `url(${headerLeft})`,
                         backgroundRepeat: 'no-repeat',
                         backgroundPosition: 'right',
                     }}
                >

                </div>
                <div className={"containerCenter"}
                     style={{
                         backgroundImage: `url(${imageCenter})`,
                         backgroundRepeat: 'no-repeat',
                         backgroundPosition: 'center',
                     }}
                >
                    <div className={"containerCenter"}
                         style={{
                             backgroundImage: `url(${imageCenterPNG})`,
                             backgroundRepeat: 'no-repeat',
                             backgroundPosition: 'center',
                         }}
                    />
                </div>
                <div className={"containerHeaderRight"}
                     style={{
                         backgroundImage: `url(${headerLeft})`,
                         backgroundRepeat: 'no-repeat',
                         backgroundPosition: 'left',
                     }}
                >
                </div>
            </div>
        // </div>
    );
};


export default ContainerResource;
