import React, {useEffect, useState} from 'react';
import '../styles/componentStyle/modalHome.scss';
import {Register} from "../Api/Register";

const ModalHomeScreen = ({ isVisible, setIsVisible, type }) => {

    const [username, setUsername] = useState(null)
    const [password, setPassword] = useState(null)
    const [result, setResult] = useState(null)

    useEffect( () => {
        console.log("username", username)
    }, [username])

    const onClick = () => {
        setIsVisible(false)
    }

    const getTitle = (type) => {
        switch (type) {
            case 1:
                return "Inscription"
            case 2:
                return "Connexion";
            default:
                return null
        }
    };

    const onValid = async () => {
        const data = {
            username: username,
            password: password
        }
        const resultat = await Register(data)
        setResult(resultat)
    }


    if (!isVisible) return null;

    return (
        <div className="modal-overlay">
            <div className="modal">
                <div className="modal-header">
                    <h2>{getTitle(type)}</h2>
                    <h2>{result}</h2>
                    <button className="close-button" onClick={onClick}>
                        <div className="close-circle"></div>
                    </button>
                </div>
                <div className="modal-content">
                    <input type="text" placeholder="Pseudo" onChange={(e) => setUsername(e.target.value)} />
                    <input type="password" placeholder="Mot de passe" onChange={(e) => setPassword(e.target.value)} />
                    {type === 1 &&
                        <input type="password" placeholder="Répéter le mot de passe" />}
                    <button onClick={onValid}>{getTitle(type)}</button>
                </div>
            </div>
        </div>
    );
};

export default ModalHomeScreen;
