import React, { useState, useEffect } from 'react';


const ContainerResource = ({ backgroundImage, imageCenter }) => {
    const [resources, setResources] = useState([
        {
            id: 1,
            type: 'gold',
            amount: 0,
            production: 1,
        },
        {
            id: 2,
            type: 'iron',
            amount: 0,
            production: 2,
        },
        {
            id: 3,
            type: 'energy',
            amount: 0,
            production: 0.5,
        },
    ]);

    useEffect(() => {
        const interval = setInterval(() => {
            resources.forEach((resource) => { //PARCOURS LE TABLEAU
                increaseResource(resource.id, resource.production);
            });
        }, 1000);
        return () => clearInterval(interval);
    }, [resources]);


    const increaseResource = (id, amount) => {
        setResources((prevResources) =>
            prevResources.map((resource) =>
                resource.id === id ? { ...resource, amount: resource.amount + amount } : resource
            )
        );
    }

    return (
        <div
             style={{
                 ...styles.containerResource,
                 backgroundImage: `url(${backgroundImage})`,
                 backgroundRepeat: 'no-repeat',
                 backgroundPosition: 'center',
             }}
        >
            <div style={styles.test}>
                <div style={styles.containerheaderLeft}>
                    {/*{resources.map((resource) => (*/}
                    {/*    <div key={resource.id} className="containerMiniResource">*/}
                    {/*      <span className="resourceText">*/}
                    {/*        {resource.type}: {resource.amount}*/}
                    {/*      </span>*/}
                    {/*    </div>*/}
                    {/*))}*/}
                </div>
                <div style={styles.containerCenter}
                     style={{
                         backgroundImage: `url(${imageCenter})`,
                         backgroundRepeat: 'no-repeat',
                         backgroundPosition: 'center',
                     }}
                />
                <div style={styles.containerheaderLeft}>
                    {/*{resources.map((resource) => (*/}
                    {/*    <div key={resource.id} className="containerMiniResource">*/}
                    {/*      <span className="resourceText">*/}
                    {/*        {resource.type}: {resource.amount}*/}
                    {/*      </span>*/}
                    {/*    </div>*/}
                    {/*))}*/}
                </div>
            </div>
        </div>
    );
};

const styles = StyleSheet.create({
    containerResource: {
        width: "100%",
        height: "25%",
        //background-color: #7d8293;
    },
    containerCenter: {
        width: "20%",
        height: "100%",
        backgroundColor: "#7d8293"
    },
    containerheaderLeft: {
        width: "40%",
        height: "100%",
        backgroundColor: "#0000cc",
    },
    test: {
        flexDirection: "row",
        width: "100%",
        height: "100%",
    },
    containerMiniResource: {
        width: 200,
        height: 50,
        background: "#0c5460",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
    },
    resourceText: {
        color: "greenyellow",
        fontSize: 16
    },
});


export default ContainerResource;
