import React, { useState } from 'react';
import './Modal.css';

const ModalHomeScreen = ({ isVisible, setIsVisible }) => {


    const onClick = () => {
        setIsVisible(false)
    }


    if (!isVisible) return null;

    return (
        <div className="modal-overlay">
            <div className="modal">
                <h2>Ma Modal</h2>
                <p>Contenu de la modal...</p>
                <button onClick={onClick}>Fermer</button>
            </div>
        </div>
    );
};

export default ModalHomeScreen;
