import React, { useState, useEffect } from 'react';
import backgroundImage from '../../Assets/Image/Header/header.png';

const ContainerResource = () => {
    const [resources, setResources] = useState([
        {
            id: 1,
            type: 'gold',
            amount: 0,
            production: 1,
        },
        {
            id: 2,
            type: 'iron',
            amount: 0,
            production: 2,
        },
        {
            id: 3,
            type: 'energy',
            amount: 0,
            production: 0.5,
        },
    ]);

    useEffect(() => {
        const interval = setInterval(() => {
            resources.forEach((resource) => { //PARCOURS LE TABLEAU
                increaseResource(resource.id, resource.production);
            });
        }, 1000);
        return () => clearInterval(interval);
    }, [resources]);


    const increaseResource = (id, amount) => {
        setResources((prevResources) =>
            prevResources.map((resource) =>
                resource.id === id ? { ...resource, amount: resource.amount + amount } : resource
            )
        );
    }

    return (
        <div className="containerResource"  style={{ backgroundImage: `url(${backgroundImage})` }}>
            {/*{resources.map((resource) => (*/}
            {/*    <div key={resource.id} className="containerMiniResource">*/}
            {/*      <span className="resourceText">*/}
            {/*        {resource.type}: {resource.amount}*/}
            {/*      </span>*/}
            {/*    </div>*/}
            {/*))}*/}
        </div>
    );
};

export default ContainerResource;



const styles = `

.containerResource {
  background-color: #7d8293;
  display: flex;
  justify-content: center; 
  align-items: center; 
}

.containerMiniResource {
  width: 200px;
  height: 50px;
  background: #0c5460;
  display: flex; /* Utilisation de flexbox pour centrer le contenu */
  justify-content: center; /* Centrer horizontalement le contenu */
  align-items: center; /* Centrer verticalement le contenu (facultatif, dépend de vos besoins) */
}

.resourceText {
  color: greenyellow;
  font-size: 16px;
}

`;

const styleTag = document.createElement('style');
styleTag.innerHTML = styles;
document.head.appendChild(styleTag);
